const chat={create:async function(t){if(!t)throw Error("logs.chat: create method first argument must be an array of messages contains atleast 1 message");t=await fetch("https://logs.chat/api/chat",{method:"POST",body:JSON.stringify({messages:t}),headers:{"Content-Type":"application/json"}}).then(t=>t.json());if(t.error)throw Error(t.message);return t},get:async function(t){if(!t&&!isNaN(t))throw Error("logs.chat: get method first argument must be a Chat ID");t=await fetch("https://logs.chat/api/chat/"+t).then(t=>t.json());return!t.error&&t.chat},exists:async function(t){if(t||isNaN(t))return await fetch(`https://logs.chat/api/chat/${t}/exists`).then(t=>t.json());throw Error("logs.chat: exists method first argument must be a Chat ID")}};